import React, { useState } from "react";
import { ws } from "./session";

function PriceLadder() {
  const [asks, setAsks] = useState(['null', 'null', 'null']);
  const [bids, setBids] = useState(null);



  ws.onmessage = function (e) {
    let data = JSON.parse(e.data);
    if (
      data.channel == "orderbook" &&
      data.market == "BTC/USD"
    ) {
        if (data.data.action == 'partial'){
      console.log(data);
      for (var i = 0; i < 3; i++) {
        asks[i] = data.data.asks[i]
      }
      setAsks(asks)
        }
    }
  };

  return (
    <div className="ladder">
      <table>
        <tbody>
          <tr className="ask">
            <td>offer 1: {asks[0]}</td>
          </tr>
          <tr className="ask">
            <td>offer 2: {asks[1]}</td>
          </tr>
          <tr className="ask">
            <td>offer 3: {asks[2]}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default PriceLadder;
