import React, { useState } from "react";
import "./APILogIn.css";

function APILogIn() {
  const [enteredAPIKey, setAPIKey] = useState();
  const symbols = ["BTC-MOVE-09-04", "BTC/USD"];
  var btc_usd = null;

  function textChangeHandler(event) {
    setAPIKey(event.target.value);
  }

  const submitHandler = (event) => {
    event.preventDefault(); //prevents reloading in the browser
    const API_KEY = enteredAPIKey;
    console.log(API_KEY);
  };

  const ws_init = function () {
    ws = new WebSocket("wss://ftx.com/ws/");

    ws.onopen = function () {
      for (s of symbols) {
        ws.send(`{"op": "subscribe", "channel": "trades", "market": "${s}"}`);
      }
    };

    ws.onclose = function () {
      setTimeout(ws_init, 5000);
    };

    ws.onerror = function (error) {
      console.log("WS error: " + error);
    };

    ws.onmessage = function (e) {
      let data = JSON.parse(e.data);
      if (data.channel == "trades" && data.type == "update") {
        handleTrades(data);
      }
    };

    const ping = function () {
      ws.send('{"op":"ping"}');
    };
    setInterval(ping, 10000);
  };
  ws_init();

  const handleTrades = function (data) {
    if (data.market == "BTC/USD") {
      btc_usd = data.data[0].price; // set the btc global for calculations, used in app.js
    }
  };

  return (
    <div>
      <form onSubmit={submitHandler}>
        <div className="api-log-in">
          <label>FTX API KEY</label>
          <input type="text" onChange={textChangeHandler} />
        </div>
        <button> Authenticate </button>
      </form>
      <div>
        {btc_usd}
      </div>
    </div>
  );
}

export default APILogIn;
