import axios, { AxiosRequestConfig } from 'axios';
import APIAUTH from './FTX API KEY.txt';
import forge from 'node-forge';


let APIKEY;
let APISECRET;

fetch(APIAUTH)
  .then(r => r.text())
  .then(text => {
    APIKEY = text.substring(10, 50)
    APISECRET = text.substring(64);
    console.log(APIKEY, APISECRET);
  });

let time = new Date().getTime();

// generate and return hash
function generateHash(plainText,secretKey){
    let hmac = forge.hmac.create();  
        hmac.start('sha256', secretKey);
        hmac.update(plainText);  
        var hashText = hmac.digest().toHex();  
        return hashText
    }      

function sendOrder(market, price, type, size){

    axios({
    method: 'POST',
    url: 'https://ftx.com/api/orders',
    data: {
        "market": market,
        "side": side,
        "price": price,
        "type": "limit",
        "size": size,
        "reduceOnly": false,
        "ioc": false,
        "postOnly": false,
        "clientId": null
    },
    headers: {
        "FTX-KEY": APIKEY,
        "FTX-TS": time,
        "FTX-SIGN": generateHash(`${new Date()}${"POST"}${"/orders"}`, API_SECRET),
    }
  })
}

export default sendOrder;
