import React, { useState } from "react";
import { ws } from "./session";

function PriceLadder() {
  const [asks, setAsks] = useState(["null", "null", "null"]);
  const [bids, setBids] = useState(null);

  ws.onmessage = function (e) {
    let data = JSON.parse(e.data);
    if (
      data.channel == "orderbook" &&
      data.market == "BTC/USD" &&
      data.data.action == "partial"
    ) {
      var newAsks = [0, 0, 0];
      for (var i = 0; i < 3; i++) {
        newAsks[i] = data.data.asks[i];
      }
      setAsks(newAsks);
      console.log(newAsks);
    } else if (data.channel == 'orderbook' && data.market == 'BTC/USD'
    && data.data.action == 'update'){
        if (data.data.asks.length > 0){
            var newAsks = asks;
            console.log(data);
            for (var i = 0; i < data.data.asks.length; i++){
                for (var j=0; j < 3; j++){
                    if (asks[j][0] == data.data.asks[i][0]){
                        newAsks[j] = data.data.asks[i];
                    }
                }
            }
            setAsks(newAsks);
        }
    }
  };

  return (
    <div className="ladder">
      <table>
        <tbody>
          <tr className="ask">
            <td>offer 1: px:{asks[0][0]}, qty:{asks[0][1]}</td>
          </tr>
          <tr className="ask">
            <td>offer 2: px:{asks[1][0]}, qty:{asks[1][1]}</td>
          </tr>
          <tr className="ask">
            <td>offer 3: px:{asks[2][0]}, qty:{asks[2][1]}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default PriceLadder;
