import React, { useState } from "react";

const symbols = ["BTC-MOVE-09-04", "BTC/USD"];

const ws_init = function (channel, markets) {

  const ws = new WebSocket("wss://ftx.com/ws/");

  ws.onopen = function () {
    for (var s of symbols) {
      ws.send(`{"op": "subscribe", "channel": channel, "market": markets}`);
    }
  };

  ws.onclose = function () {
    setTimeout(ws_init(channel, markets), 5000);
  };

  ws.onerror = function (error) {
    console.log("WS error: " + error);
  };

  const ping = function () {
    ws.send('{"op":"ping"}');
  };

  setInterval(ping, 10000);
  return ws;
};

const ws = ws_init();

export { ws };
