import React, { useState } from "react";
import { ws } from "./session";

function PriceLadder() {
  var asksArray = [
    {
      price: 0,
      qty: 0,
    },
    {
      price: 0,
      qty: 0,
    },
    {
      price: 0,
      qty: 0,
    },
  ];
  const [asks, setAsks] = useState(asksArray);
  const [bids, setBids] = useState(null);

  ws.onmessage = function (e) {
    let data = JSON.parse(e.data);
    if (
      data.channel == "orderbook" &&
      data.market == "BTC/USD" &&
      data.data.action == "partial"
    ) {
      for (var i = 0; i < 3; i++) {
        asksArray[i].price = data.data.asks[i][0];
        asksArray[i].qty = data.data.asks[i][1];
      }
      setAsks(asksArray);
      console.log(asksArray);
    } else if (
      data.channel == "orderbook" &&
      data.market == "BTC/USD" &&
      data.data.action == "update"
    ) {
      var updatedAsks = asks.map((ask) => {
        for (var i = 0; i < data.data.asks.length; i++) {
          if (ask.price == data.data.asks[i][0]) {
            return { price: ask.price, qty: ask.qty }; //gets everything that was already in item, and updates "done"
          }
        }
        return ask; // else return unmodified item
      });
      console.log(updatedAsks)
      setAsks(updatedAsks);
    }
  };

  return (
    <div className="ladder">
      <table>
        <tbody>
          {asks.map((ask, index) => (
            // Setting "index" as key because name and age can be repeated, It will be better if you assign uniqe id as key
            <tr key={index}>
              <td>
                Offer {index} px: {ask.price}, qty: {ask.qty}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PriceLadder;
